import React, { useState } from "react";
import ReactDOM from "react-dom/client";
import Tasks from "./taskData";

export default function Rectangle() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");

  const addTask = () => {
    if (newTask.trim() !== "") {
      const newTaskObject = {
        id: Date.now(),
        taskName: newTask,
        checked: false,
      };
      setTasks([...tasks, newTaskObject]);
      setNewTask("");
    }
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const checkTask = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, checked: !task.checked } : task
      )
    );
  };

  const editTask = (id,newTaskName) =>{
    setTasks(tasks.map(task=>
      task.id === id ? {...task,taskName : newTaskName} : task
    ));
  };

  return (
    <div className="rect-border">
      <div className="rectangle-og-border">
        <RectText />
        <Circle tasks={tasks} />
      </div>
      <TextInput newTask={newTask} setNewTask={setNewTask} addTask={addTask} />
      {tasks.map((task) => (
        <Task 
        key={task.id}
        task={task}
        deleteTask={deleteTask}
        checkTask={checkTask}
        editTask={editTask}
        />
      ))}
    </div>
  );
}
function RectText() {
  return (
    <div className="text-div">
      <span className="upper-text">Todo Done</span>
      <span className="lower-text">keep it up...</span>
    </div>
  );
}

function Circle({ tasks }) {
  const completedTasks = tasks.filter(task => task.checked).length;
  return (
    <div className="circle">
      <span className="number-count">{completedTasks}/{tasks.length}</span>
    </div>
  );
}

function TextInput({newTask, setNewTask, addTask}) {
  return (
    <div className="input-div">
      <input
        type="text"
        placeholder="write your next task"
        className="input-field"
      />
      <button className="add-button">+</button>
    </div>
  );
}

function Task() {
  return (
    <div className="task-box">
      <div className="check-box-text">
        <button className="radio-btn"></button>
        <span className="task-text">Task 1</span>
      </div>
      <div className="edit-delete-btn">
        <button className="edit-btn">
          <img src="/edit.png" className="edit-btn-img" />
        </button>
        <button className="delete-btn">
          <img src="/trash.png" className="delete-btn-img" />
        </button>
      </div>
    </div>
  );
}
